const viewAll = document.querySelector(".content .btn"),
    artVw = document.querySelector(".content .artView"),
    vid = document.querySelector("main video"),
    tab = document.querySelectorAll(".tab li"),
    arts = document.querySelectorAll("article");
let flag = !0;
tab.forEach((e, t) => {
    t > 0 && e.addEventListener("click", function () {
        let e = this.attributes[0].nodeValue;
        arts.forEach(t => {
            t.classList.contains(e) ? t.style.display = "block" : t.style.display = "none", artVw.className += " all", viewAll.innerText = "Back to See all Products +", flag = !1
        })
    })
}), viewAll.addEventListener("click", function () {
    flag ? (artVw.className += " all", this.innerText = "close -", flag = !1) : (arts.forEach(e => {
        e.style.display = "block"
    }), this.innerText = "All Products >", artVw.classList.remove("all"), flag = !0)
}), window.onscroll = function () {
    this.scrollY >= 100 ? vid.pause() : vid.play()
};